mapper.select:

 	select rnum, bno, btitle, btype, mid, bcreatedat, bhitcount
	from (
	    select rownum as rnum, bno, btitle, btype, mid, bcreatedat, bhitcount
	    from (
	        select bno, btitle, btype, bcontent, mid, bcreatedat, bhitcount
	        from board
	        order by bno desc
	    )
	    where rownum <= #{endRowNo}
	)
	where rnum >= #{startRowNo}

select bno, btitle, bcontent, btype, mid, bcreatedat, bhitcount, battachoname, battachtype
from board
where bno = #{bno}

mapper.update:
update board set
btitle=#{btitle}, bcontent=#{bcontent}, bupdatedat=sysdate
, battachoname=#{battachoname}, battachtype=#{battachtype}, battachdata=#{battachdata}
where bno=#{bno}
