mapper.select:


select rnum, bno, btitle, bcontent, bcreatedat, bhitcount, bsubcategory
from (
    select rownum as rnum, bno, btitle, bcontent, mid, bcreatedat, bhitcount, bsubcategory
    from (
        select bno, btitle, bcontent, mid, bcreatedat, bhitcount, bsubcategory
        from board
        order by bno desc
    )
    where rownum <= #{endRowNo}
)
where rnum >= #{startRowNo}

select bno, btitle, bcontent, mid, bcreatedat, bhitcount, battachoname, battachtype
from board
where bno = #{bno}

select battachdata
from board
where bno=#{bno}
