mapper.select:

 	select rnum, bno, btitle, mid, bcreatedat, bhitcount
	from (
	    select rownum as rnum, bno, btitle, mid, bcreatedat, bhitcount
	    from (
	        select bno, btitle, bcontent, mid, bcreatedat, bhitcount
	        from board
	        order by bno desc
	    )
	    where rownum <= #{endRowNo}
	)
	where rnum >= #{startRowNo}
