mapper.select:
select mid, mpassword, mname, mphone, memail, mrole
from member
where mid=#{mid}

select maddress
from address
where mid=#{mid}

select mzipcode
from address
where mid=#{mid}

select mpassword
from member
where mid=#{mid}

mapper.update:
