mapper.insert:
insert into orders (
	ordid, mid, ordtotalamount, ordtotalprice,
	ordrequest, ordstatus, ordcreatedat, 
	ordreceivername, ordreceiveraddress, ordreceiverzipcode, ordreceiverphone
)
values(
	#{ordid}, #{mid}, #{ordtotalamount}, #{ordtotalprice}, 
	#{ordrequest}, #{ordstatus}, SYSDATE, 
	#{ordreceivername}, #{ordreceiveraddress}, #{ordreceiverzipcode}, #{ordreceiverphone}
)

mapper.select:
select rownum as seq, ord.ordid, mid, prd.prdname, prdbrand, ordtotalamount, ordtotalprice, ordrequest, ordstatus, ordpostnum, ordcreatedat, ordreceivername, ordreceiveraddress, ordreceiverzipcode, ordreceiverphone, odt.prdcode, odtamount, odt.prdprice, (SELECT COUNT(*) FROM review rev WHERE rev.ordid = ord.ordid AND rev.prdcode = odt.prdcode AND rev.mid = ord.mid) AS revcount
from orders ord, ordersdetail odt, product prd
where ord.ordid = odt.ordid and odt.prdcode = prd.prdcode and mid=#{mid}

mapper.insert:
insert into payment (
	ordid, pmtdate
)
values(
	#{ordid}, sysdate
)

mapper.select:
select count(*)
from orders
where mid=#{mid}
