mapper.insert:
insert into orders (
	ordid, mid, ordtotalamount, ordtotalprice,
	ordrequest, ordstatus, ordcreatedat, 
	ordreceivername, ordreceiveraddress, ordreceiverzipcode, ordreceiverphone
)
values(
	#{ordid}, #{mid}, #{ordtotalamount}, #{ordtotalprice}, 
	#{ordrequest}, #{ordstatus}, SYSDATE, 
	#{ordreceivername}, #{ordreceiveraddress}, #{ordreceiverzipcode}, #{ordreceiverphone}
)

insert into payment (
	ordid, pmtdate
)
values(
	#{ordid}, sysdate
)

mapper.select:
select count(*)
from orders
where mid=#{mid}
