mapper.insert:
insert into orders (
	ordid, mid, ordtotalamount, ordtotalprice,
	ordrequest, ordstatus, ordcreatedat, 
	ordreceivername, ordreceiveraddress, ordreceiverzipcode, ordreceiverphone
)
values(
	#{ordid}, #{mid}, #{ordtotalamount}, #{ordtotalprice}, 
	#{ordrequest}, #{ordstatus}, SYSDATE, 
	#{ordreceivername}, #{ordreceiveraddress}, #{ordreceiverzipcode}, #{ordreceiverphone}
)

mapper.select:
select rownum as seq, ord.ordid, mid, prd.prdname, prdbrand, ordtotalamount, ordtotalprice, ordrequest, ordstatus, ordpostnum, ordcreatedat, ordreceivername, ordreceiveraddress, ordreceiverzipcode, ordreceiverphone, odt.prdcode, odtamount, odt.prdprice
from orders ord, ordersdetail odt, product prd
where ord.ordid = odt.ordid and odt.prdcode = prd.prdcode and mid=#{mid}
